import "./AdminDashboard.css";

import { useState, useEffect } from "react";

import { Link, useNavigate } from "react-router-dom";

import axios from "axios";

import { connect } from "react-redux";

const AdminDashboard = ({ getToken, getAdmin }) => {
 
  const navigate = useNavigate();
  const [employees, setEmployees] = useState([]);
  useEffect(() => {
    axios
      .get(
        "http://localhost:4000/admin/getall",
        {
          headers: {
            "Content-Type": "application/json",
            "auth-token": localStorage.getItem("token"),
          },
        }
      )
      .then((response) => {
        setEmployees(response.data);
      });
  });
  const deleteEmployee = (id) => {
    axios.delete(
      `http://localhost:4000/admin/delete/${id}`,
      {
        headers: {
          "Content-Type": "application/json",
          "auth-token": getToken,
        },
      }
    );
  };
  return (
    <>
      <div className="admin-dashboard">
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <div>
            <div>Admin</div>
            <div>
              {getAdmin.firstName} {getAdmin.lastName}
            </div>
            <div>{getAdmin.email}</div>
          </div>
        </div>
        <div>
          <Link to="/" style={{ color: "blue" }}>
            Home
          </Link>
          {/* <Link to="/admin/add" style={{ color: "blue" }}>
            Add Employee
          </Link> */}
          <button to="/admin/add">
          Add Employee
          </button>
        </div>
        
        <h2>All Employees</h2>
        {employees.length === 0 && <p>No Employees</p>}
        {employees.length > 0 && (
          <div className="employee-details">
            <h3>
              <strong>First Name</strong>
            </h3>
            <h3>
              <strong>Last Name</strong>
            </h3>
            <h3>
              <strong>Email</strong>
            </h3>
          </div>
        )}
        {employees.map((employee) => (
          <div key={employee._id} className="employee">
            <div>{employee.firstName}</div>
            <div>{employee.lastName}</div>
            <div>{employee.email}</div>
            <div
              className="edit"
              onClick={() => {
                navigate(`/admin/edit/${employee._id}`);
              }}
            >
              Edit
            </div>
            <div
              className="delete"
              onClick={() => {
                deleteEmployee(employee._id);
              }}
            >
              Delete
            </div>
          </div>
        ))}
        <div>
         
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    getToken: state.signin.token,
   // getAdmin: state.signin.admin,
   getAdmin:localStorage.getItem("adminData")
  };
};

export default connect(mapStateToProps, null)(AdminDashboard);
